/*
        TXT by HTML5 UP
        html5up.net | @n33co
        Free for personal and commercial use under the CCA 3.0 license (html5up.net/license)
*/

/*********************************************************************************/
/* Basic                                                                         */
/*********************************************************************************/

body,input,textarea,select
                        {
                            line-height: 1.75em;
                            font-size: 10pt;
                            letter-spacing: 0;
                        }

h2, h3, h4, h5, h6
                    {
                        font-size: 1.5em;
                        margin: 0 0 0.5em 0;
                    }

h2.major
{
    text-align: left;
    margin: 0.775em 0 0.75em 0;
}

h2.major span
{
    top: -0.775em;
    padding: 0 1em 0 0;
}

h3
{
    font-size: 1.25em;
}

/* Section/Article */

section,
article
    {
        clear: both;
        margin: 0 0 2.5em 0 !important;
    }

header
{
}

header p
{
    font-size: 1.5em;
    line-height: 1.5em;
}

/* Button */

input[type="button"],
input[type="submit"],
input[type="reset"],
.button
 {
     font-size: 1.5em;
     width: 100%;
     padding: 0.75em 0 0.75em 0;
     margin-top: 0.5em;
     text-align: center;
 }

/* List */

ul.special
{
}

ul.special li
{
    margin: 0.5em;
}

ul.special li:hover
                  {
                      top: 0;
                  }

ul.special a
{
    width: 8em;
    height: 8em;
    border-radius: 4.75em;
}

ul.special a:before
                 {
                     font-size: 48px;
                 }

ul.contact
{
}

ul.contact li
{
    margin: 0 0.25em 0.35em 0;
    top: 0 !important;
}

ul.actions
{
}

ul.actions li
{
    display: block;
    padding: 0;
    margin: 0.75em 0 0 0;
}

ul.actions li:first-child
                  {
                      margin-top: 0;
                  }

ul.menu
{
}

ul.menu li
{
    display: block;
    border: 0 !important;
    padding: 0 !important;
    margin: 0 !important;
}

/* Box */

.box
{
}

.box.highlight
{
}

.box.highlight .special
{
}

.box.highlight h2
{
    font-size: 2.25em;
    line-height: 1.25em;
    margin: 0 0 0.5em 0;
}

.box.highlight header > p
{
    margin: 0 0 0.75em 0;
}

.box.highlight br
{
    display: none;
}

.box.post-summary
{
    margin: 0 !important;
}

.box.post-summary h3
{
    font-size: 1.25em;
}

.box.post
{
}

.box.post h3
{
    font-size: 2.25em;
    line-height: 1.25em;
    margin: 0 0 0.5em 0;
}

.box.post header > p
{
    margin: 0 0 0.75em 0;
}

.box.post ul.meta
{
    margin: 0 0 0.5em 0;
}

.box.post header
{
    margin: 0 0 3em 0;
}

.box.page-content
{
}

.box.page-content h2
{
    font-size: 2.25em;
    line-height: 1.25em;
    margin: 0 0 0.5em 0;
}

.box.page-content header > p
{
    margin: 0 0 0.75em 0;
}

.box.page-content ul.meta
{
    margin: 0 0 0.5em 0;
}

.box.page-content header
{
    margin: 0 0 3em 0;
}

/*********************************************************************************/
/* Sidebar + Content                                                             */
/*********************************************************************************/

.sidebar
{
    border-top: solid 2px rgb(234, 231, 231);
    padding-top: 5em;
    margin-top: 5em;

    border-bottom: solid 2px rgb(234, 231, 231);
    padding-bottom: 5em;
}

.sidebar > section
{
    margin: 0 0 5em 0 !important;
}

.sidebar > section:last-child
                       {
                           margin: 0 !important;
                       }

.content
{
    padding-top: 3em;
}

.homepage .content
{
    padding-top: 0;
}

/*********************************************************************************/
/* Wrappers                                                                      */
/*********************************************************************************/

#banner-wrapper
{
    padding: 9em 0 5em 0;
}

#main-wrapper
{
    border-top-width: 0px;
    border-bottom-width: 5px;
}

.homepage #main-wrapper
{
    border-top-width: 15px;
}

/*********************************************************************************/
/* Header                                                                        */
/*********************************************************************************/

#header
{
    display: none;
}

/*********************************************************************************/
/* Nav                                                                           */
/*********************************************************************************/

#nav
{
    display: none;
}

/*********************************************************************************/
/* Banner                                                                        */
/*********************************************************************************/

#banner
{
    padding: 2.5em 20px;
    margin: 0 !important;
}

#banner h2
{
    font-size: 2.25em;
    line-height: 1.25em;
    margin: 0 0 0.5em 0;
}

#banner p
{
    margin: 0 0 0.5em 0;
}

/*********************************************************************************/
/* Main                                                                          */
/*********************************************************************************/

#main
{
    padding: 1em 20px 1em 20px;
}

#main > .row
{
    padding: 2em 0 2em 0;
}

/*********************************************************************************/
/* Footer                                                                        */
/*********************************************************************************/

#footer
{
    padding: 4em 20px 2em 20px;
}

#footer > .row
{
    padding: 1em 0 1em 0;
}

/*********************************************************************************/
/* Copyright                                                                     */
/*********************************************************************************/

#copyright
{
    width: 100%;
    display: block;
}

#copyright .menu
{
}

#copyright .menu li
{
    line-height: inherit;
}

/*********************************************************************************/
/* Mobile UI                                                                     */
/*********************************************************************************/

#titleBar
{
    background-color: rgba(38, 21, 21, 0.8);
    background-image: url('images/overlay.png');
}

#titleBar .title
{
    display: block;
    position: relative;
    font-family: 'Open Sans Condensed', sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 1.25em;
    line-height: 44px;
    color: #fff;
    z-index: 1000;
    text-align: center;
}

#titleBar .toggle
{
    position: absolute;
    left: 0;
    top: 0;
    width: 80px;
    height: 60px;
    z-index: 1001;
}

#titleBar .toggle:before
                      {
                          display: inline-block;
                          font-family: FontAwesome;
                          text-decoration: none;
                          font-style: normal;
                          font-weight: normal;
                          -webkit-font-smoothing:antialiased;
                          -moz-osx-font-smoothing:grayscale;

                          position: absolute;
                          left: 0;
                          top: 0;
                          background: rgb(75, 16, 16);
                          width: 44px;
                          height: 44px;
                          content: '\f0c9';
                          color: #fff;
                          text-align: center;
                          line-height: 44px;
                          font-size: 20px;
                      }

#titleBar .toggle:active:before
                             {
                                 background-color: rgb(205, 155, 155);
                             }

#navPanel
{
    background-color:		rgb(60, 53, 53);
    background-image:		url('images/overlay.png'),		-moz-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.5));
    background-image:		url('images/overlay.png'),		-webkit-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.5));
    background-image:		url('images/overlay.png'),		-o-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.5));
    background-image:		url('images/overlay.png'),		-ms-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.5));
    background-image:		url('images/overlay.png'),		linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0.5));
    background-repeat:		repeat,							no-repeat;
    background-size:		auto,							100% 100%;
    font-family: 'Open Sans Condensed', sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 1.25em;
    box-shadow: inset -3px 0px 10px 0px rgba(0,0,0,0.25);
}

#navPanel nav
{
    position: relative;
    z-index: 1000;
}

#navPanel .link
{
    display: block;
    color: inherit;
    text-decoration: none;
    height: 44px;
    line-height: 44px;
    padding: 0 1em 0 1em;
    color: #888;
    color: rgba(255,255,255,0.5);
    border-top: solid 2px rgba(93, 87, 87, 0.15);
}

#navPanel .link:first-child
                    {
                        border-top: 0;
                    }

#navPanel .indent-1 { display: inline-block; width: 1em; }
#navPanel .indent-2 { display: inline-block; width: 2em; }
#navPanel .indent-3 { display: inline-block; width: 3em; }
#navPanel .indent-4 { display: inline-block; width: 4em; }
#navPanel .indent-5 { display: inline-block; width: 5em; }
#navPanel .depth-0 { color: #fff; }

