diskText:
@charset 'UTF-8';
@import url("font-awesome.min.css");
@import url("http://fonts.googleapis.com/css?family=Open+Sans:400,400italic,700,700italic|Open+Sans+Condensed:700");

/*
        TXT by HTML5 UP
        html5up.net | @n33co
        Free for personal and commercial use under the CCA 3.0 license (html5up.net/license)
*/

/*********************************************************************************/
/* Basic                                                                         */
/*********************************************************************************/

body
{
    background: rgb(245, 242, 242);
}

body.is-loading *
{
    -moz-transition: none !important;
    -webkit-transition: none !important;
    -o-transition: none !important;
    -ms-transition: none !important;
    transition: none !important;
    -moz-animation: none !important;
    -webkit-animation: none !important;
    -o-animation: none !important;
    -ms-animation: none !important;
    animation: none !important;
}

body,input,textarea,select
                        {
                            font-family: 'Open Sans', sans-serif;
                            font-weight: 400;
                            color: rgb(119, 107, 107);
                            font-size: 11.5pt;
                            line-height: 2em;
                        }

h1, h2, h3, h4, h5, h6
                        {
                            font-family: 'Open Sans Condensed', sans-serif;
                            font-weight: 700;
                            text-transform: uppercase;
                            color: rgb(119, 107, 107);
                        }

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a
                                 {
                                     text-decoration: none;
                                     color: inherit;
                                 }


h2.major
{
    position: relative;
    border-top: solid 5px rgb(234, 231, 231);
}

h2.major span
{
    background: #fff;
    position: relative;
    display: inline-block;
}

a
{
    color: rgb(118,0,0);
    text-decoration: underline;
}

a:hover
      {
          text-decoration: none;
      }

b, strong
       {
           font-weight: 700;
           color: rgb(119, 107, 107);
       }

i, em
       {
           font-style: italic;
       }

br.clear
{
    clear: both;
}

sub
{
    position: relative;
    top: 0.5em;
    font-size: 0.8em;
}

sup
{
    position: relative;
    top: -0.5em;
    font-size: 0.8em;
}

hr
{
    border: 0;
    border-top: solid 2px rgb(234, 231, 231);
    margin: 0 0 3em 0;
}

blockquote
{
    border-left: solid 5px rgb(234, 231, 231);
    padding: 1em 0 1em 1.5em;
    font-style: italic;
}

p, ul, ol, dl, table
                   {
                       margin-bottom: 3em;
                   }

/* Section/Article */

section,
article
    {
        margin-bottom: 6em;
    }

section > :last-child,
article > :last-child
               {
                   margin-bottom: 0;
               }

section:last-child,
article:last-child
            {
                margin-bottom: 0;
            }

header > p
{
    display: block;
    font-family: 'Open Sans Condensed', sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    color: rgb(202, 193, 193);
}

/* Image */

.image
{
    position: relative;
    display: inline-block;
    border-radius: 8px;
}

.image img
{
    display: block;
    width: 100%;
    border-radius: 8px;
}

.image:before
           {
               content: '';
               position: absolute;
               left: 0;
               top: 0;
               width: 100%;
               height: 100%;
               background: url('images/overlay.png');
               border-radius: 8px;
           }

.image.fit
{
    display: block;
    width: 100%;
}

.image.featured
{
    display: block;
    width: 100%;
    margin: 0 0 2em 0;
}

.image.left
{
    float: left;
    margin: 0 2em 2em 0;
}

.image.centered
{
    display: block;
    margin: 0 0 2em 0;
}

.image.centered img
{
    margin: 0 auto;
    width: auto;
}

/* Button */

input[type="button"],
input[type="submit"],
input[type="reset"],
.button
 {
     -webkit-appearance: none;
     display: inline-block;
     font-family: 'Open Sans Condensed', sans-serif;
     font-weight: 700;
     text-transform: uppercase;
     text-decoration: none;
     background: rgb(75, 16, 16);
     color: #fff;
     border: 0;
     line-height: 1em;
     border-radius: 8px;
     outline: 0;
     cursor: pointer;
     -moz-transition: background-color .2s ease-in-out;
     -webkit-transition: background-color .2s ease-in-out;
     -o-transition: background-color .2s ease-in-out;
     -ms-transition: background-color .2s ease-in-out;
     transition: background-color .2s ease-in-out;
 }

input[type="button"]:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
.button:hover
            {
                background: rgb(237, 187, 187);
            }

input[type="button"]:active,
input[type="submit"]:active,
input[type="reset"]:active,
.button:active
            {
                background: rgb(205,155,155);
            }

input[type="button"].alt,
input[type="submit"].alt,
input[type="reset"].alt,
.button.alt
 {
     background: rgb(202, 193, 193);
 }

input[type="button"].alt:hover,
input[type="submit"].alt:hover,
input[type="reset"].alt:hover,
.button.alt:hover
                {
                    background: rgb(213, 204, 204);
                }

