// JavaScript Document

if (!Object.keys) {
    Object.keys = (function() {
        'use strict';
        var hasOwnProperty = Object.prototype.hasOwnProperty,
            hasDontEnumBug = !({ toString: null }).propertyIsEnumerable('toString'),
            dontEnums = [
                'toString',
                'toLocaleString',
                'valueOf',
                'hasOwnProperty',
                'isPrototypeOf',
                'propertyIsEnumerable',
                'constructor'
            ],
            dontEnumsLength = dontEnums.length;

        return function(obj) {
            if (typeof obj !== 'object' && (typeof obj !== 'function' || obj === null)) {
                throw new TypeError('Object.keys called on non-object');
            }

            var result = [], prop, i;

            for (prop in obj) {
                if (hasOwnProperty.call(obj, prop)) {
                    result.push(prop);
                }
            }

            if (hasDontEnumBug) {
                for (i = 0; i < dontEnumsLength; i++) {
                    if (hasOwnProperty.call(obj, dontEnums[i])) {
                        result.push(dontEnums[i]);
                    }
                }
            }
            return result;
        };
    }());
}

//Main menu Javascript area
var timeout	= 500;
var closetimer	= 0;
var ddmenuitem	= 0;

// open hidden layer
function mopen(id)
{
    // cancel close timer
    mcancelclosetime();

    // close old layer
    mclose()

    // get new layer and show it
    ddmenuitem = $('#'+id);
    ddmenuitem.show();

}
// close showed layer
function mclose()
{
    if(ddmenuitem.length > 0){
       ddmenuitem.hide();
    }
}

// go close timer
function mclosetime()
{
    closetimer = window.setTimeout(mclose, timeout);
}

// cancel close timer
function mcancelclosetime()
{
    if(closetimer)
    {
        window.clearTimeout(closetimer);
        closetimer = null;
    }
}


// close layer when click-out
document.onclick = mclose;

function makevisible1(){
    $("#error1")[0].type= "text";
}
function panelvisible(){
    $(".hidden").show();
    $(".visible").hide();
}

//Initialize the Javascript Enviroment
function init() {
    completeField=$("#call")[0];
    localStorage.removeItem("tb_baseinfo");
    localStorage.removeItem("tb_mobileinfo");
    localStorage.removeItem("tb_portableinfo");
    localStorage.removeItem("bsstuffhere");
}

//check the callsign against iC database and pull appropriate Information.
function doCallsignLookup() {
    var datastring = {callsign:$('#call').val().trim()};
    $.ajax({
        type: "POST",
        url: "./bin/test_registered.php",
        data: datastring,
        cache: false,
        success: function(result) {
            var validator = JSON.parse(result);
            if (validator[0].count == 1) {
              panelvisible();
            }else{
                $.ajax({
                    type: "POST",
                    url: "./bin/validate_call.php",
                    data: datastring,
                    cache: false,
                    success: function(result) {
                        var validator1 = JSON.parse(result);
                        if (validator1 == false || validator1.length < 1) {
                            $('.hidden_notfound').show();
                            $('.visible').hide();
                        }else{
                            Object.keys(validator1[0]).forEach(function(k){
                              $('#'+k).val(validator1[0][k].trim());
                            });
                            // style
                            $('.hidden_detail').show();
                            $('.visible').hide();
                            $('.hidden_notfound').hide();
                        }
                    }
                });
            }
        }
    });
    return false;
}

//function to display detail screens
function startDetail(){
	if($("#street").val()==""){
		alert("You must enter your street address");
	}else if($("#city").val()==""){
		alert("You must enter your City");
	}else if($("#province").val()==""){
		alert("You must enter your Province");
	}else if($("#postal_code").val()==""){
		alert("You must enter your Postal Code");
	}else{
		$('.hidden_detail').hide();
		$('.hidden_initial').show();
		$('.hidden_hr').show();
		$('.hidden_master').show();
		$('.p_info').show();
	}
}
function changetab(tab) {
    $('ul#navigation > li > a').removeClass('selected');
    $('#tab'+tab).addClass('selected');
    $('table.master_border tr > td > div').hide();
    $('table.master_border tr > td > div:nth-child('+tab+')').show();
    if (tab == 3) {
        loadModeSystem('tb_baseinfo');
    }else if(tab ==4){
        loadModeSystem('tb_mobileinfo');
    }else if(tab == 5) {
        loadModeSystem('tb_portableinfo');
    }
}

function dofaenable(tab) {
    $('#facertificate')[0].disabled = (tab == 0);
}
function perscell(prov,cell) {
	var e = $(prov).val();
  $(cell).prop('disabled',e==0).val('');
}
function changeCase(strcall){
	var e = $(strcall).val();
	$(strcall).val(e.toUpperCase());
}
function loadModeSystem(tabhit) {
    if (JSON.parse(localStorage.getItem(tabhit)) == null){
        var hasrun={tabhit1:tabhit};
        localStorage.setItem(tabhit, JSON.stringify(hasrun));
        $.ajax({
            type: "POST",
            url: "./bin/get_mode_cols.php",
            cache: false,
            success: function(result) {
                var data = JSON.parse(result);
                $('#'+tabhit+' thead tr').empty();
                $.each(data[0], function(k,v){
                    $('#'+tabhit+' thead tr').append($('<td>'+k+'</td>'));
                });
                var even=0;
                $('#'+tabhit+' tbody').empty();
                $.each(data,function(k,e){
                    even = (even+1)%2;
                    var tr = $('<tr class="'+['even','odd'][even]+'" />');
                    $('#'+tabhit+' tbody').append(tr);
                    tr.append($('<td>'+e.BAND+'</td>'));
                    var bnd = e.BAND;
                    delete e.BAND;
                    var stationname = tabhit.replace('tb_','');
                    $.each(e, function(k,e){
                        tr.append($('<td><input type="checkbox" value="1" '+(e? 'checked': '')+' name="'+stationname+"["+bnd+"]["+k+']"/></td>'));
                    });
                });
            }
        });
    }
}

function submitForm(){
  $.ajax({
    url:'/bin/submit_form.php',
    data:$('form').serializeArray(),
    method:'post',
    dataType:'json',
    success:function(result){
      console.log(result);
    }
  });
  return false;
}

